document.addEventListener("DOMContentLoaded",(function(){generateQuote(),particlesJS.load("particles","assets/particles-config.json")}));const loader=document.getElementById("loader"),quote=document.getElementById("quote"),author=document.getElementById("author");document.querySelector(".lang-switch__button_en").onclick=()=>{currentLanguage="en",document.querySelector(".lang-switch__button_en").classList.add("lang-switch__button_active"),document.querySelector(".lang-switch__button_ru").classList.remove("lang-switch__button_active"),generateQuote()},document.querySelector(".lang-switch__button_ru").onclick=()=>{currentLanguage="ru",document.querySelector(".lang-switch__button_ru").classList.add("lang-switch__button_active"),document.querySelector(".lang-switch__button_en").classList.remove("lang-switch__button_active"),generateQuote()};let currentLanguage="en",currentText="",currentAuthor="";function generateQuote(){enableLoader(),fetchQuote(currentLanguage).then(e=>{renderQuote(e)}).then(disableLoader)}function renderQuote(e){currentText=e.quoteText,currentAuthor=e.quoteAuthor,document.getElementById("quote").innerText=currentText;""!==currentAuthor?(document.getElementById("author").innerText="â€” "+currentAuthor,document.getElementById("author").style.display="block"):document.getElementById("author").style.display="none"}function tweet(){tweetQuote(currentText,currentAuthor)}function enableLoader(){loader.hidden=!1,quote.hidden=!0,author.style.display="none"}function disableLoader(){loader.hidden=!0,quote.hidden=!1,author.style.display="inherit"}